	snapSvgHelper.regionClicked = function(regionName){
		$('.back-button').show();
		snapSvgHelper.activeRegion = regionName;
		
		var animatedProperty = regionName + "RegionLargeSize";
		var matchRegexp = new RegExp(regionName + ".*");

		snapSvgHelper.allRegions.forEach(function(value){
			if(value.match(matchRegexp)){
				snapSvgHelper[regionName + "Region"].animate({d: snapSvgHelper[animatedProperty]}, 500);
			}else{
				$(snapSvgHelper[value].node).fadeOut(500);
			}
		});

	};// End of regionClicked

